        renderItem={({ item }) => (
          <View style={{ marginBottom: 20, alignItems: 'center' }}>
            <Image source={{ uri: item }} style={{ width: 200, height: 200 }} />
          </View>
        )}
      />

    </View>
  );
};




export default ImagePickerExample;
